mapper.insert:
INSERT
INTO
    SMI_SCHE
(
    SMI_SEQ
, SCH_NM
, SCH_DATE
, SCH_TIME
, SCH_CNT
, SCH_LOC
)
VALUES(
          #{smiSeq}
      , #{schNm}
      , #{schDate}
      , #{schTime}
      , #{schCnt}
      , #{schLoc}
      )

mapper.update:
UPDATE
    somoim.SMI_SCHE
SET
    SMI_SEQ = #{smiSeq}
  , SCH_NM = #{schNm}
  , SCH_DATE = #{schDate}
  , SCH_TIME = #{schTime}
  , SCH_CNT = #{schCnt}
  , SCH_LOC = #{schLoc}
  , FRST_WRT_DTIM = CURRENT_TIMESTAMP
  , LAST_CHNG_DTIM = CURRENT_TIMESTAMP
WHERE
    SCH_SEQ = #{schSeq}

